$(function() {

  //Burger Menu
$('.burger').on( "click", function() {
  $(this).toggleClass("close");

  $menuTitle = $(".menu-title");

    if($menuTitle.text() == "Menu") {
        $menuTitle.text("Close").css("color","white");
    } else {
        $menuTitle.text("Menu").css("color","blue");
    }

  $('#dropdown-menu').toggleClass("activeMenu");;

  });

  // Slide up panel

  $('.slide-up').hide();

  $('.box-link').hover(function(e) {
    e.preventDefault();
    $(this).find('.slide-up').slideToggle();

  });

  // Circle hover

  $('.circle-help').hover(function(e) {

    $menuTitle = $(".circle-help").find('p');
    e.preventDefault();

    if( $menuTitle.text() == "How can we help?") {

      $menuTitle.text("Please choose your interest...").css("color","blue").css("font-size","24px");
    }else{
      $menuTitle.text("How can we help?").css("color","white").css("font-size","42px");;
    }

  });

  // **** Scroll Magic ***

// Init Scroll Magic
// Create a controller and save it in a variable - tells the browswe
// to use the scroll bar and trigger animation
$controller = new ScrollMagic.Controller();

// Build a scene (Nav Scene)
$navScene = new ScrollMagic.Scene({
//Options added Here
  triggerElement: '.fix-trigger',
  triggerHook: '0.4'

})

// Comment this in for testing
.addIndicators({name:"test-header"})

// State which element with - class we want to change
.setClassToggle('header','active')
// .setPin('.headers-wrap')
// And where we what the class to change too
.addTo($controller);



}); //
