$(function() {

  //Burger Menu
$('.burger').on( "click", function() {
  $(this).toggleClass("close");

  $menuTitle = $(".menu-title")

    if($menuTitle.text() == "Menu") {
        $menuTitle.text("Close").css("color","white");
    } else {
        $menuTitle.text("Menu").css("color","blue");
    }

  $('#dropdown-menu').toggleClass("activeMenu");;
});

  $('.slide-up').hide();

    $('.box-link').hover(function(e) {
      e.preventDefault();
      $(this).find('.slide-up').slideToggle();

    });


});
